import './App.css'
import { useState, useEffect } from 'react'

function App() {

  const [cars, setCars] = useState("")
  const [newCar, setnewCar] = useState("")

  //fetch all cars from mongodb
  useEffect(() => {
    fetch("http://localhost:3000/cars")
    .then((res) => res.json())
    .then((data) => {
      setCars(data)
    })
  }, [])



  return (
    <div className="App">

    {/* make div with 3 inputs that will change state of new car on change and button will add a car to database */}
    <div className="newCar">
      <input type="text" placeholder="name" onChange={(e) => setnewCar({...newCar, name: e.target.value})}/>
      <input type="text" placeholder="model" onChange={(e) => setnewCar({...newCar, model: e.target.value})}/>
      <input type="text" placeholder="color" onChange={(e) => setnewCar({...newCar, color: e.target.value})}/>
      <button onClick={() => {
        fetch("http://localhost:3000/newCar", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(newCar),
        })
        .then((res) => res.json())
        .then((data) => {
          setCars([...cars, data])
        })
      }}>Add Car</button>


    </div>
    {/* make one input with button that will send put request with id and color to update the color
     */}

    <div className="updateCar">
      <input type="text" placeholder="id" onChange={(e) => setnewCar({...newCar, _id: e.target.value})}/>
      <input type="text" placeholder="color" onChange={(e) => setnewCar({...newCar, color: e.target.value})}/>
      <button onClick={() => {
        fetch(`http://localhost:3000/updateCar/${newCar._id}`, {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(newCar),
        })
        .then((res) => res.json())
        .then((data) => {
          setCars([...cars, data])
        })
      }}>Update Car</button>

      </div>
    <div className="cars">
      {cars && cars.map((car) => (
        <div className="car" key={car._id}>
          <h2>{car.name}</h2>
          <h2>{car._id}</h2>

          <h3>{car.model}</h3>
          <h3>{car.color}</h3>
          </div>
      ))}

       </div>
       </div>


  );
      }


export default App;
